import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import GeneralContext from "./GeneralContext";
import "./BuyActionWindow.css";

const BuyActionWindow = ({ uid, stockData }) => {
  const [stockQuantity, setStockQuantity] = useState(1);
  const [stockPrice, setStockPrice] = useState(stockData?.currentPrice || 0.0);
  const [orderType, setOrderType] = useState("MARKET"); // MARKET, LIMIT, SL, SL-M

  const { closeBuyWindow } = useContext(GeneralContext);

  const handleBuyClick = async () => {
    try {
      await axios.post("http://localhost:5000/newOrder", {
        name: uid,
        qty: stockQuantity,
        price: orderType === "MARKET" ? stockData?.currentPrice : stockPrice,
        mode: "BUY",
        orderType: orderType,
      });

      closeBuyWindow();
    } catch (error) {
      console.error("Order failed:", error);
    }
  };

  const handleCancelClick = () => {
    closeBuyWindow();
  };

  // Calculate margin required
  const calculateMargin = () => {
    const price = orderType === "MARKET" ? stockData?.currentPrice : stockPrice;
    return (price * stockQuantity).toFixed(2);
  };

  const orderTypes = [
    { value: "MARKET", label: "Market" },
    { value: "LIMIT", label: "Limit" },
    { value: "SL", label: "Stop Loss" },
    { value: "SL-M", label: "SL-Market" }
  ];

  return (
    <div className="trading-window">
      {/* Header */}
      <div className="window-header">
        <div className="stock-info">
          <h3 className="stock-name">{uid}</h3>
          <span className="stock-price">₹{stockData?.currentPrice || 0.00}</span>
        </div>
        <button className="close-btn" onClick={handleCancelClick}>
          ×
        </button>
      </div>

      {/* Order Type Selection */}
      <div className="order-type-section">
        <div className="order-type-tabs">
          {orderTypes.map((type) => (
            <button
              key={type.value}
              className={`order-type-tab ${orderType === type.value ? 'active' : ''}`}
              onClick={() => setOrderType(type.value)}
            >
              {type.label}
            </button>
          ))}
        </div>
      </div>

      {/* Input Section */}
      <div className="input-section">
        <div className="input-group">
          <label htmlFor="quantity">Quantity</label>
          <div className="quantity-controls">
            <button 
              className="quantity-btn"
              onClick={() => setStockQuantity(Math.max(1, stockQuantity - 1))}
            >
              −
            </button>
            <input
              type="number"
              id="quantity"
              value={stockQuantity}
              onChange={(e) => setStockQuantity(Math.max(1, parseInt(e.target.value) || 1))}
              min="1"
            />
            <button 
              className="quantity-btn"
              onClick={() => setStockQuantity(stockQuantity + 1)}
            >
              +
            </button>
          </div>
        </div>

        {orderType !== "MARKET" && (
          <div className="input-group">
            <label htmlFor="price">Price (₹)</label>
            <input
              type="number"
              id="price"
              step="0.05"
              value={stockPrice}
              onChange={(e) => setStockPrice(parseFloat(e.target.value) || 0)}
            />
          </div>
        )}

        {/* Order Summary */}
        <div className="order-summary">
          <div className="summary-item">
            <span>Estimated amount:</span>
            <span>₹{calculateMargin()}</span>
          </div>
          <div className="summary-item">
            <span>Brokerage:</span>
            <span>₹{(calculateMargin() * 0.0001).toFixed(2)}</span>
          </div>
          <div className="summary-item total">
            <span>Total required:</span>
            <span>₹{(parseFloat(calculateMargin()) + parseFloat(calculateMargin() * 0.0001)).toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="action-buttons">
        <button className="btn-buy" onClick={handleBuyClick}>
          <span className="btn-icon">📈</span>
          Buy {uid}
        </button>
        <button className="btn-cancel" onClick={handleCancelClick}>
          Cancel
        </button>
      </div>

      {/* Quick Quantity Options */}
      <div className="quick-quantity">
        <span>Quick quantity:</span>
        <div className="quantity-options">
          {[10, 25, 50, 100].map((qty) => (
            <button
              key={qty}
              className="quantity-option"
              onClick={() => setStockQuantity(qty)}
            >
              {qty}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BuyActionWindow;
