import React, { useState, useEffect } from "react";
import axios from "axios";
import { VerticalGraph } from "./VerticalGraph";
import "./Holdings.css";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedStock, setExpandedStock] = useState(null);

  useEffect(() => {
    fetchHoldings();
  }, []);

  const fetchHoldings = async () => {
    try {
      setLoading(true);
      const response = await axios.get("http://localhost:5000/allHoldings");
      setAllHoldings(response.data);
    } catch (error) {
      console.error("Error fetching holdings:", error);
    } finally {
      setLoading(false);
    }
  };

  const toggleStockDetails = (index) => {
    setExpandedStock(expandedStock === index ? null : index);
  };

  // Calculate summary data
  const calculateSummary = () => {
    const totalInvestment = allHoldings.reduce((sum, stock) => sum + (stock.avg * stock.qty), 0);
    const currentValue = allHoldings.reduce((sum, stock) => sum + (stock.price * stock.qty), 0);
    const pnl = currentValue - totalInvestment;
    const pnlPercentage = totalInvestment > 0 ? (pnl / totalInvestment) * 100 : 0;

    return {
      totalInvestment,
      currentValue,
      pnl,
      pnlPercentage
    };
  };

  const summary = calculateSummary();

  const chartData = {
    labels: allHoldings.map(stock => stock.name),
    datasets: [
      {
        label: "Current Value",
        data: allHoldings.map(stock => stock.price * stock.qty),
        backgroundColor: allHoldings.map(stock => {
          const profit = (stock.price * stock.qty) - (stock.avg * stock.qty) >= 0;
          return profit ? "rgba(72, 187, 120, 0.7)" : "rgba(229, 62, 62, 0.7)";
        }),
        borderColor: allHoldings.map(stock => {
          const profit = (stock.price * stock.qty) - (stock.avg * stock.qty) >= 0;
          return profit ? "rgba(72, 187, 120, 1)" : "rgba(229, 62, 62, 1)";
        }),
        borderWidth: 1,
      },
    ],
  };

  if (loading) {
    return (
      <div className="holdings-loading">
        <div className="loading-spinner"></div>
        <p>Loading your holdings...</p>
      </div>
    );
  }

  return (
    <div className="holdings-container">
      {/* Header */}
      <div className="holdings-header">
        <div className="header-content">
          <h1 className="holdings-title">
            Holdings <span className="holdings-count">({allHoldings.length})</span>
          </h1>
          <p className="holdings-subtitle">Your investment portfolio overview</p>
        </div>
        <div className="header-actions">
          <button className="btn btn-outline" onClick={fetchHoldings}>
            <span className="btn-icon">ðŸ”„</span>
            Refresh
          </button>
          <button className="btn btn-primary">
            <span className="btn-icon">ðŸ“Š</span>
            Export
          </button>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="summary-cards">
        <div className="summary-card">
          <div className="card-icon">ðŸ’°</div>
          <div className="card-content">
            <h3 className="card-value">â‚¹{summary.totalInvestment.toLocaleString('en-IN', { minimumFractionDigits: 2 })}</h3>
            <p className="card-label">Total Investment</p>
          </div>
        </div>

        <div className="summary-card">
          <div className="card-icon">ðŸ“ˆ</div>
          <div className="card-content">
            <h3 className="card-value">â‚¹{summary.currentValue.toLocaleString('en-IN', { minimumFractionDigits: 2 })}</h3>
            <p className="card-label">Current Value</p>
          </div>
        </div>

        <div className={`summary-card ${summary.pnl >= 0 ? 'profit' : 'loss'}`}>
          <div className="card-icon">{summary.pnl >= 0 ? 'ðŸ’¹' : 'ðŸ”»'}</div>
          <div className="card-content">
            <h3 className="card-value">
              â‚¹{Math.abs(summary.pnl).toLocaleString('en-IN', { minimumFractionDigits: 2 })} 
              <span className="percentage">({summary.pnlPercentage.toFixed(2)}%)</span>
            </h3>
            <p className="card-label">{summary.pnl >= 0 ? 'Profit & Loss' : 'Loss'}</p>
          </div>
        </div>
      </div>

      {/* Holdings Table */}
      <div className="holdings-table-container">
        <div className="table-header">
          <h3>Your Holdings</h3>
          <div className="table-actions">
            <button className="action-btn">Filter</button>
            <button className="action-btn">Sort</button>
          </div>
        </div>

        <div className="table-responsive">
          <table className="holdings-table">
            <thead>
              <tr>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg. Cost</th>
                <th>LTP</th>
                <th>Current Value</th>
                <th>P&L</th>
                <th>Net Chg.</th>
                <th>Day Chg.</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {allHoldings.map((stock, index) => {
                const currentValue = stock.price * stock.qty;
                const investment = stock.avg * stock.qty;
                const pnl = currentValue - investment;
                const pnlPercentage = investment > 0 ? (pnl / investment) * 100 : 0;
                const isProfit = pnl >= 0;
                const dayProfit = !stock.isLoss;

                return (
                  <React.Fragment key={index}>
                    <tr className="holding-row" onClick={() => toggleStockDetails(index)}>
                      <td className="instrument-cell">
                        <div className="stock-info">
                          <span className="stock-name">{stock.name}</span>
                          <span className="stock-symbol">NSE</span>
                        </div>
                      </td>
                      <td>{stock.qty}</td>
                      <td>â‚¹{stock.avg.toFixed(2)}</td>
                      <td>â‚¹{stock.price.toFixed(2)}</td>
                      <td>â‚¹{currentValue.toFixed(2)}</td>
                      <td className={isProfit ? 'profit' : 'loss'}>
                        â‚¹{pnl.toFixed(2)} ({pnlPercentage.toFixed(2)}%)
                      </td>
                      <td className={isProfit ? 'profit' : 'loss'}>{stock.net}</td>
                      <td className={dayProfit ? 'profit' : 'loss'}>{stock.day}</td>
                      <td className="actions-cell">
                        <button className="action-btn small">Buy</button>
                        <button className="action-btn small">Sell</button>
                      </td>
                    </tr>

                    {expandedStock === index && (
                      <tr className="details-row">
                        <td colSpan="9">
                          <div className="stock-details">
                            <div className="detail-item">
                              <span>Investment:</span>
                              <span>â‚¹{investment.toFixed(2)}</span>
                            </div>
                            <div className="detail-item">
                              <span>Current Value:</span>
                              <span>â‚¹{currentValue.toFixed(2)}</span>
                            </div>
                            <div className="detail-item">
                              <span>P&L:</span>
                              <span className={isProfit ? 'profit' : 'loss'}>
                                â‚¹{pnl.toFixed(2)} ({pnlPercentage.toFixed(2)}%)
                              </span>
                            </div>
                            <div className="detail-actions">
                              <button className="btn btn-small">View Chart</button>
                              <button className="btn btn-small">Transaction History</button>
                            </div>
                          </div>
                        </td>
                      </tr>
                    )}
                  </React.Fragment>
                );
              })}
            </tbody>
          </table>
        </div>

        {allHoldings.length === 0 && (
          <div className="empty-state">
            <div className="empty-icon">ðŸ“­</div>
            <h3>No holdings found</h3>
            <p>You haven't invested in any stocks yet.</p>
            <button className="btn btn-primary">Start Investing</button>
          </div>
        )}
      </div>

      {/* Chart Section */}
      {allHoldings.length > 0 && (
        <div className="chart-section">
          <div className="section-header">
            <h3>Portfolio Distribution</h3>
            <div className="chart-legend">
              <div className="legend-item">
                <span className="legend-color profit"></span>
                <span>Profitable</span>
              </div>
              <div className="legend-item">
                <span className="legend-color loss"></span>
                <span>Loss</span>
              </div>
            </div>
          </div>
          <VerticalGraph data={chartData} />
        </div>
      )}
    </div>
  );
};

export default Holdings;
