import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const location = useLocation();

  // Get user data
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch("http://localhost:5000/current-user", {
          method: "GET",
          credentials: "include",
        });
        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
        }
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };
    fetchUser();
  }, []);

  const handleProfileClick = () => {
    setIsProfileDropdownOpen(!isProfileDropdownOpen);
  };

  const handleLogout = async () => {
    try {
      await fetch("http://localhost:5000/logout", {
        method: "GET",
        credentials: "include",
      });
      window.location.href = "/";
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  const menuItems = [
    { path: "/dashboard", label: "Dashboard", icon: "📊" },
    { path: "/dashboard/orders", label: "Orders", icon: "📝" },
    { path: "/dashboard/holdings", label: "Holdings", icon: "💼" },
    { path: "/dashboard/positions", label: "Positions", icon: "⚡" },
    { path: "/dashboard/funds", label: "Funds", icon: "💰" },
    { path: "/dashboard/apps", label: "Apps", icon: "📱" }
  ];

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + "/");
  };

  const getInitials = (username) => {
    return username ? username.charAt(0).toUpperCase() : "U";
  };

  return (
    <div className="menu-container">
      {/* Logo Section */}
      <div className="menu-header">
        <Link to="/dashboard" className="logo-link">
          <img 
            src="https://zerodha.com/static/images/logo.svg" 
            alt="Zerodha" 
            className="menu-logo"
          />
        </Link>
        <span className="console-label">Console</span>
      </div>

      {/* Navigation Menu */}
      <nav className="menu-nav">
        <ul className="menu-list">
          {menuItems.map((item, index) => (
            <li key={index} className="menu-item">
              <Link
                to={item.path}
                className={`menu-link ${isActive(item.path) ? 'active' : ''}`}
              >
                <span className="menu-icon">{item.icon}</span>
                <span className="menu-label">{item.label}</span>
                {isActive(item.path) && <div className="active-indicator"></div>}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* Profile Section */}
      <div className="menu-footer">
        <div className="profile-section">
          <div 
            className={`profile-container ${isProfileDropdownOpen ? 'active' : ''}`}
            onClick={handleProfileClick}
          >
            <div className="avatar">
              {getInitials(user?.username)}
            </div>
            <div className="profile-info">
              <span className="username">{user?.username || "USERID"}</span>
              <span className="user-id">{user?.username ? `ID: ${user.username}` : "Loading..."}</span>
            </div>
            <span className="dropdown-arrow">▼</span>
          </div>

          {/* Profile Dropdown */}
          {isProfileDropdownOpen && (
            <div className="profile-dropdown">
              <div className="dropdown-item">
                <span className="dropdown-icon">👤</span>
                <span>Profile Settings</span>
              </div>
              <div className="dropdown-item">
                <span className="dropdown-icon">⚙️</span>
                <span>Account Preferences</span>
              </div>
              <div className="dropdown-item">
                <span className="dropdown-icon">🛡️</span>
                <span>Security</span>
              </div>
              <hr className="dropdown-divider" />
              <div className="dropdown-item" onClick={handleLogout}>
                <span className="dropdown-icon">🚪</span>
                <span>Logout</span>
              </div>
            </div>
          )}
        </div>

        {/* Quick Stats */}
        <div className="quick-stats">
          <div className="stat-item">
            <span className="stat-label">Available</span>
            <span className="stat-value">₹25,000</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Today's P&L</span>
            <span className="stat-value profit">+₹1,250</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
