import React, { useState, useEffect } from "react";
import { Route, Routes, useNavigate, useLocation } from "react-router-dom";
import Apps from "./Apps";
import Funds from "./Funds";
import Holdings from "./Holdings";
import Orders from "./Orders";
import Positions from "./Positions";
import Summary from "./Summary";
import WatchList from "./WatchList";
import { GeneralContextProvider } from "./GeneralContext";
import "./Dashboard.css";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [authChecked, setAuthChecked] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Check authentication - FIXED VERSION
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:5000/api/current_user", {
        method: "GET",
        credentials: "include",
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Auth response:", data); // Debug log

        // FIX: Only redirect if there's an actual error, not when user is null
        if (data.user) {
          setUser(data.user);
        } else {
          // User is not authenticated, redirect to login
          console.log("No user found, redirecting to login");
          window.location.href = "/login";
          return;
        }
      } else {
        // HTTP error (4xx, 5xx)
        console.log("HTTP error, redirecting to login");
        window.location.href = "/login";
      }
    } catch (error) {
      console.error("Auth check error:", error);
      // Only redirect on network errors, not on user null cases
      if (error.name !== 'TypeError') { // Network errors
        window.location.href = "/login";
      }
    } finally {
      setLoading(false);
      setAuthChecked(true);
    }
  };

  const navigationItems = [
    { path: "/dashboard", label: "Dashboard", icon: "📊", component: Summary },
    { path: "/dashboard/orders", label: "Orders", icon: "📝", component: Orders },
    { path: "/dashboard/holdings", label: "Holdings", icon: "💼", component: Holdings },
    { path: "/dashboard/positions", label: "Positions", icon: "⚡", component: Positions },
    { path: "/dashboard/funds", label: "Funds", icon: "💰", component: Funds },
    { path: "/dashboard/apps", label: "Apps", icon: "📱", component: Apps }
  ];

  const handleLogout = async () => {
    try {
      await fetch("http://localhost:5000/logout", {
        method: "GET",
        credentials: "include",
      });
      // Clear local state
      setUser(null);
      // Redirect to home/login
      window.location.href = "/";
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  // Show loading only for initial auth check
  if (loading && !authChecked) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Checking authentication...</p>
      </div>
    );
  }

  // If auth check completed but no user, show nothing (will redirect)
  if (!user && authChecked) {
    return (
      <div className="dashboard-loading">
        <p>Redirecting to login...</p>
      </div>
    );
  }

  return (
    <GeneralContextProvider>
      <div className="dashboard-container">
        {/* Top Navigation Bar */}
        <nav className="dashboard-nav">
          <div className="nav-brand">
            <img 
              src="https://zerodha.com/static/images/logo.svg" 
              alt="Zerodha" 
              className="nav-logo"
              onClick={() => navigate("/dashboard")}
            />
            <span className="nav-title">Console</span>
          </div>

          <div className="nav-actions">
            <div className="user-info">
              <span className="user-welcome">Welcome, {user?.username || "Trader"}</span>
              <div className="account-balance">
                <span className="balance-label">Available:</span>
                <span className="balance-amount">₹25,000.00</span>
              </div>
            </div>

            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>

            <button 
              className="mobile-menu-btn"
              onClick={() => setSidebarOpen(!sidebarOpen)}
            >
              ☰
            </button>
          </div>
        </nav>

        <div className="dashboard-layout">
          {/* Sidebar */}
          <aside className={`dashboard-sidebar ${sidebarOpen ? 'open' : ''}`}>
            <div className="sidebar-header">
              <h3>Trading Console</h3>
              <button 
                className="sidebar-close"
                onClick={() => setSidebarOpen(false)}
              >
                ×
              </button>
            </div>

            <nav className="sidebar-nav">
              {navigationItems.map((item) => (
                <button
                  key={item.path}
                  className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
                  onClick={() => {
                    navigate(item.path);
                    setSidebarOpen(false);
                  }}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-label">{item.label}</span>
                </button>
              ))}
            </nav>

            {/* Quick Actions */}
            <div className="quick-actions">
              <h4>Quick Actions</h4>
              <div className="action-buttons">
                <button className="action-btn buy-btn" onClick={() => navigate("/dashboard")}>
                  Buy
                </button>
                <button className="action-btn sell-btn" onClick={() => navigate("/dashboard")}>
                  Sell
                </button>
                <button className="action-btn fund-btn" onClick={() => navigate("/dashboard/funds")}>
                  Add Funds
                </button>
              </div>
            </div>

            {/* Market Status */}
            <div className="market-status">
              <div className="status-item">
                <span className="status-label">NSE</span>
                <span className="status-value green">● Live</span>
              </div>
              <div className="status-item">
                <span className="status-label">BSE</span>
                <span className="status-value green">● Live</span>
              </div>
            </div>
          </aside>

          {/* Overlay for mobile */}
          {sidebarOpen && (
            <div 
              className="sidebar-overlay"
              onClick={() => setSidebarOpen(false)}
            ></div>
          )}

          {/* Main Content */}
          <main className="dashboard-main">
            {/* Watchlist Section */}
            <section className="watchlist-section">
              <WatchList />
            </section>

            {/* Page Content */}
            <section className="content-section">
              <Routes>
                <Route path="/dashboard" element={<Summary />} />
                <Route path="/dashboard/orders" element={<Orders />} />
                <Route path="/dashboard/holdings" element={<Holdings />} />
                <Route path="/dashboard/positions" element={<Positions />} />
                <Route path="/dashboard/funds" element={<Funds />} />
                <Route path="/dashboard/apps" element={<Apps />} />
              </Routes>
            </section>
          </main>
        </div>
      </div>
    </GeneralContextProvider>
  );
};

export default Dashboard;
